package io.capawesome.capacitorjs.plugins.contacts.classes;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import com.getcapacitor.JSObject;
import io.capawesome.capacitorjs.plugins.contacts.ContactsPlugin;
import io.capawesome.capacitorjs.plugins.contacts.enums.PhoneNumberType;
import org.json.JSONObject;

public class PhoneNumber {

    @Nullable
    private final Boolean isPrimary;

    @Nullable
    private final String label;

    @NonNull
    private final PhoneNumberType type;

    @NonNull
    private final String value;

    public PhoneNumber(@Nullable Boolean isPrimary, @Nullable String label, @NonNull PhoneNumberType type, @NonNull String value) {
        this.isPrimary = isPrimary;
        this.label = label;
        this.type = type;
        this.value = value;
    }

    public PhoneNumber(@NonNull JSObject data) throws Exception {
        this.isPrimary = data.getBoolean("isPrimary", false);
        this.label = data.getString("label");
        this.type = getTypeFromJSObject(data);
        this.value = getValueFromJSObject(data);
    }

    public static PhoneNumber fromJSONObject(JSONObject data) throws Exception {
        return new PhoneNumber(JSObject.fromJSONObject(data));
    }

    @Nullable
    public Boolean getIsPrimary() {
        return isPrimary;
    }

    @Nullable
    public String getLabel() {
        return label;
    }

    @NonNull
    public PhoneNumberType getType() {
        return type;
    }

    @NonNull
    public String getValue() {
        return value;
    }

    @NonNull
    public JSObject toJSObject() {
        JSObject result = new JSObject();
        result.put("isPrimary", isPrimary);
        result.put("label", label);
        result.put("type", type.toString());
        result.put("value", value);
        return result;
    }

    @NonNull
    private PhoneNumberType getTypeFromJSObject(@NonNull JSObject data) throws Exception {
        String typeAsString = data.getString("type");
        if (typeAsString == null) {
            return PhoneNumberType.OTHER;
        }
        PhoneNumberType type = PhoneNumberType.fromString(typeAsString);
        if (type == null) {
            throw new Exception(ContactsPlugin.ERROR_TYPE_INVALID);
        }
        return type;
    }

    @NonNull
    private String getValueFromJSObject(@NonNull JSObject data) throws Exception {
        String value = data.getString("value");
        if (value == null) {
            throw new Exception(ContactsPlugin.ERROR_VALUE_MISSING);
        }
        return value;
    }
}
