package io.capawesome.capacitorjs.plugins.contacts.classes.options;

import androidx.annotation.NonNull;
import com.getcapacitor.PluginCall;
import io.capawesome.capacitorjs.plugins.contacts.ContactsPlugin;

public class DeleteContactByIdOptions {

    @NonNull
    private final String id;

    public DeleteContactByIdOptions(@NonNull PluginCall call) throws Exception {
        this.id = getIdFromCall(call);
    }

    @NonNull
    public String getId() {
        return id;
    }

    @NonNull
    private String getIdFromCall(@NonNull PluginCall call) throws Exception {
        String id = call.getString("id");
        if (id == null) {
            throw new Exception(ContactsPlugin.ERROR_ID_MISSING);
        }
        return id;
    }
}
