package io.capawesome.capacitorjs.plugins.contacts.classes.options;

import androidx.annotation.NonNull;
import com.getcapacitor.PluginCall;
import io.capawesome.capacitorjs.plugins.contacts.ContactsHelper;
import io.capawesome.capacitorjs.plugins.contacts.ContactsPlugin;
import io.capawesome.capacitorjs.plugins.contacts.enums.ContactField;
import java.util.List;

public class GetContactByIdOptions {

    @NonNull
    private final List<ContactField> fields;

    @NonNull
    private final String id;

    public GetContactByIdOptions(@NonNull PluginCall call) throws Exception {
        this.fields = ContactsHelper.getFieldsFromCall(call);
        this.id = getIdFromCall(call);
    }

    public GetContactByIdOptions(@NonNull String id, @NonNull PluginCall call) throws Exception {
        this.fields = ContactsHelper.getFieldsFromCall(call);
        this.id = id;
    }

    @NonNull
    public List<ContactField> getFields() {
        return fields;
    }

    @NonNull
    public String getId() {
        return id;
    }

    @NonNull
    private String getIdFromCall(@NonNull PluginCall call) throws Exception {
        String id = call.getString("id");
        if (id == null) {
            throw new Exception(ContactsPlugin.ERROR_ID_MISSING);
        }
        return id;
    }
}
