package io.capawesome.capacitorjs.plugins.contacts.classes.results;

import android.accounts.Account;
import androidx.annotation.NonNull;
import com.getcapacitor.JSArray;
import com.getcapacitor.JSObject;
import io.capawesome.capacitorjs.plugins.contacts.interfaces.Result;
import java.util.ArrayList;

public class GetAccountsResult implements Result {

    @NonNull
    private final ArrayList<Account> accounts;

    public GetAccountsResult(@NonNull ArrayList<Account> accounts) {
        this.accounts = accounts;
    }

    @Override
    public JSObject toJSObject() {
        JSObject result = new JSObject();
        JSArray accountsResult = new JSArray();

        for (Account account : accounts) {
            JSObject value = new JSObject();
            value.put("name", account.name);
            value.put("type", account.type);
            accountsResult.put(value);
        }

        result.put("accounts", accountsResult);
        return result;
    }
}
