package io.capawesome.capacitorjs.plugins.contacts.classes.results;

import androidx.annotation.Nullable;
import com.getcapacitor.JSObject;
import io.capawesome.capacitorjs.plugins.contacts.classes.Contact;
import io.capawesome.capacitorjs.plugins.contacts.interfaces.Result;
import org.json.JSONObject;

public class GetContactByIdResult implements Result {

    @Nullable
    private final Contact contact;

    public GetContactByIdResult(@Nullable Contact contact) {
        this.contact = contact;
    }

    @Nullable
    public Contact getContact() {
        return contact;
    }

    @Override
    public JSObject toJSObject() {
        JSObject result = new JSObject();
        result.put("contact", contact == null ? JSONObject.NULL : contact.toJSObject());
        return result;
    }
}
