package io.capawesome.capacitorjs.plugins.contacts.classes.results;

import androidx.annotation.NonNull;
import com.getcapacitor.JSArray;
import com.getcapacitor.JSObject;
import io.capawesome.capacitorjs.plugins.contacts.classes.Contact;
import io.capawesome.capacitorjs.plugins.contacts.interfaces.Result;
import java.util.List;

public class PickContactsResult implements Result {

    @NonNull
    private final List<Contact> contacts;

    public PickContactsResult(@NonNull List<Contact> contacts) {
        this.contacts = contacts;
    }

    @Override
    public JSObject toJSObject() {
        JSArray contactsResult = new JSArray();
        for (Contact contact : contacts) {
            JSObject contactResult = contact.toJSObject();
            contactsResult.put(contactResult);
        }

        JSObject result = new JSObject();
        result.put("contacts", contactsResult);
        return result;
    }
}
