package io.capawesome.capacitorjs.plugins.contacts.enums;

import android.provider.ContactsContract;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;

public enum PhoneNumberType {
    ASSISTANT("ASSISTANT"),
    CALLBACK("CALLBACK"),
    CAR("CAR"),
    COMPANY_MAIN("COMPANY_MAIN"),
    CUSTOM("CUSTOM"),
    FAX_HOME("FAX_HOME"),
    FAX_OTHER("FAX_OTHER"),
    FAX_WORK("FAX_WORK"),
    HOME("HOME"),
    ISDN("ISDN"),
    MAIN("MAIN"),
    MMS("MMS"),
    MOBILE("MOBILE"),
    OTHER("OTHER"),
    PAGER("PAGER"),
    RADIO("RADIO"),
    TELEX("TELEX"),
    TTY_TDD("TTY_TDD"),
    WORK("WORK"),
    WORK_MOBILE("WORK_MOBILE"),
    WORK_PAGER("WORK_PAGER");

    private final String value;

    private PhoneNumberType(String value) {
        this.value = value;
    }

    @Nullable
    public static PhoneNumberType fromInt(@Nullable Integer value) {
        if (value == null) {
            return null;
        }
        return switch (value) {
            case ContactsContract.CommonDataKinds.Phone.TYPE_CUSTOM -> CUSTOM;
            case ContactsContract.CommonDataKinds.Phone.TYPE_HOME -> HOME;
            case ContactsContract.CommonDataKinds.Phone.TYPE_MOBILE -> MOBILE;
            case ContactsContract.CommonDataKinds.Phone.TYPE_WORK -> WORK;
            case ContactsContract.CommonDataKinds.Phone.TYPE_FAX_WORK -> FAX_WORK;
            case ContactsContract.CommonDataKinds.Phone.TYPE_FAX_HOME -> FAX_HOME;
            case ContactsContract.CommonDataKinds.Phone.TYPE_PAGER -> PAGER;
            case ContactsContract.CommonDataKinds.Phone.TYPE_OTHER -> OTHER;
            case ContactsContract.CommonDataKinds.Phone.TYPE_CALLBACK -> CALLBACK;
            case ContactsContract.CommonDataKinds.Phone.TYPE_CAR -> CAR;
            case ContactsContract.CommonDataKinds.Phone.TYPE_COMPANY_MAIN -> COMPANY_MAIN;
            case ContactsContract.CommonDataKinds.Phone.TYPE_ISDN -> ISDN;
            case ContactsContract.CommonDataKinds.Phone.TYPE_MAIN -> MAIN;
            case ContactsContract.CommonDataKinds.Phone.TYPE_OTHER_FAX -> FAX_OTHER;
            case ContactsContract.CommonDataKinds.Phone.TYPE_RADIO -> RADIO;
            case ContactsContract.CommonDataKinds.Phone.TYPE_TELEX -> TELEX;
            case ContactsContract.CommonDataKinds.Phone.TYPE_TTY_TDD -> TTY_TDD;
            case ContactsContract.CommonDataKinds.Phone.TYPE_WORK_MOBILE -> WORK_MOBILE;
            case ContactsContract.CommonDataKinds.Phone.TYPE_WORK_PAGER -> WORK_PAGER;
            case ContactsContract.CommonDataKinds.Phone.TYPE_ASSISTANT -> ASSISTANT;
            case ContactsContract.CommonDataKinds.Phone.TYPE_MMS -> MMS;
            default -> null;
        };
    }

    public static PhoneNumberType fromString(String value) {
        for (PhoneNumberType type : PhoneNumberType.values()) {
            if (type.value.equals(value)) {
                return type;
            }
        }
        return null;
    }

    public int toInt() {
        return switch (this.value) {
            case "CUSTOM" -> ContactsContract.CommonDataKinds.Phone.TYPE_CUSTOM;
            case "HOME" -> ContactsContract.CommonDataKinds.Phone.TYPE_HOME;
            case "MOBILE" -> ContactsContract.CommonDataKinds.Phone.TYPE_MOBILE;
            case "WORK" -> ContactsContract.CommonDataKinds.Phone.TYPE_WORK;
            case "FAX_WORK" -> ContactsContract.CommonDataKinds.Phone.TYPE_FAX_WORK;
            case "FAX_HOME" -> ContactsContract.CommonDataKinds.Phone.TYPE_FAX_HOME;
            case "PAGER" -> ContactsContract.CommonDataKinds.Phone.TYPE_PAGER;
            case "CALLBACK" -> ContactsContract.CommonDataKinds.Phone.TYPE_CALLBACK;
            case "CAR" -> ContactsContract.CommonDataKinds.Phone.TYPE_CAR;
            case "COMPANY_MAIN" -> ContactsContract.CommonDataKinds.Phone.TYPE_COMPANY_MAIN;
            case "ISDN" -> ContactsContract.CommonDataKinds.Phone.TYPE_ISDN;
            case "MAIN" -> ContactsContract.CommonDataKinds.Phone.TYPE_MAIN;
            case "FAX_OTHER" -> ContactsContract.CommonDataKinds.Phone.TYPE_OTHER_FAX;
            case "RADIO" -> ContactsContract.CommonDataKinds.Phone.TYPE_RADIO;
            case "TELEX" -> ContactsContract.CommonDataKinds.Phone.TYPE_TELEX;
            case "TTY_TDD" -> ContactsContract.CommonDataKinds.Phone.TYPE_TTY_TDD;
            case "WORK_MOBILE" -> ContactsContract.CommonDataKinds.Phone.TYPE_WORK_MOBILE;
            case "WORK_PAGER" -> ContactsContract.CommonDataKinds.Phone.TYPE_WORK_PAGER;
            case "ASSISTANT" -> ContactsContract.CommonDataKinds.Phone.TYPE_ASSISTANT;
            case "MMS" -> ContactsContract.CommonDataKinds.Phone.TYPE_MMS;
            default -> ContactsContract.CommonDataKinds.Phone.TYPE_OTHER;
        };
    }

    @NonNull
    public String toString() {
        return value;
    }
}
