package io.capawesome.capacitorjs.plugins.contacts.enums;

import android.provider.ContactsContract;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;

public enum PostalAddressType {
    CUSTOM("CUSTOM"),
    HOME("HOME"),
    OTHER("OTHER"),
    WORK("WORK");

    private final String value;

    private PostalAddressType(String value) {
        this.value = value;
    }

    @Nullable
    public static PostalAddressType fromInt(@Nullable Integer value) {
        if (value == null) {
            return null;
        }
        return switch (value) {
            case ContactsContract.CommonDataKinds.StructuredPostal.TYPE_CUSTOM -> CUSTOM;
            case ContactsContract.CommonDataKinds.StructuredPostal.TYPE_HOME -> HOME;
            case ContactsContract.CommonDataKinds.StructuredPostal.TYPE_WORK -> WORK;
            case ContactsContract.CommonDataKinds.StructuredPostal.TYPE_OTHER -> OTHER;
            default -> null;
        };
    }

    public static PostalAddressType fromString(String value) {
        for (PostalAddressType type : PostalAddressType.values()) {
            if (type.value.equals(value)) {
                return type;
            }
        }
        return null;
    }

    public int toInt() {
        return switch (this.value) {
            case "CUSTOM" -> ContactsContract.CommonDataKinds.StructuredPostal.TYPE_CUSTOM;
            case "HOME" -> ContactsContract.CommonDataKinds.StructuredPostal.TYPE_HOME;
            case "WORK" -> ContactsContract.CommonDataKinds.StructuredPostal.TYPE_WORK;
            default -> ContactsContract.CommonDataKinds.StructuredPostal.TYPE_OTHER;
        };
    }

    @NonNull
    public String toString() {
        return value;
    }
}
