package io.capawesome.capacitorjs.plugins.contacts.enums;

import android.provider.ContactsContract;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;

public enum UrlAddressType {
    BLOG("BLOG"),
    CUSTOM("CUSTOM"),
    FTP("FTP"),
    HOME("HOME"),
    HOMEPAGE("HOMEPAGE"),
    PROFILE("PROFILE"),
    OTHER("OTHER"),
    WORK("WORK");

    private final String value;

    private UrlAddressType(String value) {
        this.value = value;
    }

    @NonNull
    public static UrlAddressType fromInt(@Nullable Integer value) {
        if (value == null) {
            return OTHER;
        }
        return switch (value) {
            case ContactsContract.CommonDataKinds.Website.TYPE_BLOG -> BLOG;
            case ContactsContract.CommonDataKinds.Website.TYPE_CUSTOM -> CUSTOM;
            case ContactsContract.CommonDataKinds.Website.TYPE_FTP -> FTP;
            case ContactsContract.CommonDataKinds.Website.TYPE_HOME -> HOME;
            case ContactsContract.CommonDataKinds.Website.TYPE_HOMEPAGE -> HOMEPAGE;
            case ContactsContract.CommonDataKinds.Website.TYPE_PROFILE -> PROFILE;
            case ContactsContract.CommonDataKinds.Website.TYPE_WORK -> WORK;
            case ContactsContract.CommonDataKinds.Website.TYPE_OTHER -> OTHER;
            default -> OTHER;
        };
    }

    @Nullable
    public static UrlAddressType fromString(String value) {
        for (UrlAddressType type : UrlAddressType.values()) {
            if (type.value.equals(value)) {
                return type;
            }
        }
        return null;
    }

    public int toInt() {
        return switch (this.value) {
            case "BLOG" -> ContactsContract.CommonDataKinds.Website.TYPE_BLOG;
            case "CUSTOM" -> ContactsContract.CommonDataKinds.Website.TYPE_CUSTOM;
            case "FTP" -> ContactsContract.CommonDataKinds.Website.TYPE_FTP;
            case "HOME" -> ContactsContract.CommonDataKinds.Website.TYPE_HOME;
            case "HOMEPAGE" -> ContactsContract.CommonDataKinds.Website.TYPE_HOMEPAGE;
            case "PROFILE" -> ContactsContract.CommonDataKinds.Website.TYPE_PROFILE;
            case "WORK" -> ContactsContract.CommonDataKinds.Website.TYPE_WORK;
            default -> ContactsContract.CommonDataKinds.Website.TYPE_OTHER;
        };
    }

    @NonNull
    public String toString() {
        return value;
    }
}
