import Capacitor
import Contacts

@objc public class Group: NSObject {
    private let id: String
    private let name: String

    init(_ group: CNGroup) {
        self.id = group.identifier
        self.name = group.name
    }

    init(_ group: CNMutableGroup) {
        self.id = group.identifier
        self.name = group.name
    }

    public func toJSObject() -> JSObject {
        var result = JSObject()
        result["name"] = name
        result["id"] = id
        return result
    }
}
