import Capacitor

@objc public class CreateGroupOptions: NSObject {
    let name: String

    init(_ call: CAPPluginCall) throws {
        guard let group = call.getObject("group") else {
            throw CustomError.groupMissing
        }
        self.name = try CreateGroupOptions.getNameFromGroup(group)
    }

    private static func getNameFromGroup(_ group: JSObject) throws -> String {
        guard let name = group["name"] as? String else {
            throw CustomError.groupNameMissing
        }
        return name
    }
}
