import Capacitor

@objc public class DisplayCreateContactOptions: NSObject {
    let contact: Contact?

    init(_ call: CAPPluginCall) throws {
        self.contact = try DisplayCreateContactOptions.getContactFromCall(call)
    }

    private static func getContactFromCall(_ call: CAPPluginCall) throws -> Contact? {
        guard let contact = call.getObject("contact") else {
            return nil
        }
        return try Contact(contact)
    }
}
