import Foundation
import Contacts
import Capacitor

@objc public class CreateGroupResult: NSObject, Result {
    private let group: CNMutableGroup

    init(_ group: CNMutableGroup) {
        self.group = group
    }

    public func toJSObject() -> AnyObject {
        var result = JSObject()
        result["id"] = group.identifier
        return result as AnyObject
    }
}
