import Foundation
import Contacts
import Capacitor

@objc public class GetGroupByIdResult: NSObject, Result {
    private let group: CNGroup?

    init(group: CNGroup?) {
        self.group = group
    }

    public func toJSObject() -> AnyObject {
        var result = JSObject()
        if let group = self.group {
            result["group"] = Group(group).toJSObject()
        } else {
            result["group"] = nil
        }
        return result as AnyObject
    }
}
