import Foundation
import Contacts
import Capacitor

@objc public class GetGroupsResult: NSObject, Result {
    private let groups: [CNGroup]

    init(groups: [CNGroup]) {
        self.groups = groups
    }

    public func toJSObject() -> AnyObject {
        var result = JSObject()
        var groupsArray = JSArray()

        self.groups.forEach { groupsArray.append(Group($0).toJSObject()) }
        result["groups"] = groupsArray

        return result as AnyObject
    }
}
