import Foundation

public enum CustomError: Error {
    case birthdayDayMissing
    case birthdayMonthMissing
    case contactMissing
    case contactNotFound
    case displayCreateContactCanceled
    case displayUpdateContactByIdCanceled
    case groupMissing
    case groupNameMissing
    case groupNotFound
    case idMissing
    case privacyDescriptionsMissing
    case readContactsPermissionDenied
    case typeInvalid
    case valueMissing
    case unknown
}

extension CustomError: LocalizedError {
    public var errorDescription: String? {
        switch self {
        case .birthdayDayMissing:
            return NSLocalizedString("Birthday day must be provided.", comment: "birthdayDayMissing")
        case .birthdayMonthMissing:
            return NSLocalizedString("Birthday month must be provided.", comment: "birthdayMonthMissing")
        case .contactMissing:
            return NSLocalizedString("contact must be provided.", comment: "contactMissing")
        case .contactNotFound:
            return NSLocalizedString("Contact not found.", comment: "contactNotFound")
        case .displayCreateContactCanceled:
            return NSLocalizedString("displayCreateContact canceled.", comment: "displayCreateContactCanceled")
        case .displayUpdateContactByIdCanceled:
            return NSLocalizedString("displayUpdateContactById canceled.", comment: "displayUpdateContactByIdCanceled")
        case .groupMissing:
            return NSLocalizedString("group must be provided.", comment: "groupMissing")
        case .groupNameMissing:
            return NSLocalizedString("name must be provided.", comment: "groupNameMissing")
        case .groupNotFound:
            return NSLocalizedString("Group not found.", comment: "groupNotFound")
        case .idMissing:
            return NSLocalizedString("id must be provided.", comment: "idMissing")
        case .privacyDescriptionsMissing:
            return NSLocalizedString("One or more privacy descriptions are missing.", comment: "privacyDescriptionsMissing")
        case .readContactsPermissionDenied:
            return NSLocalizedString("readContacts permission denied.", comment: "readContactsPermissionDenied")
        case .typeInvalid:
            return NSLocalizedString("type is invalid.", comment: "typeInvalid")
        case .valueMissing:
            return NSLocalizedString("value must be provided.", comment: "valueMissing")
        case .unknown:
            return NSLocalizedString("An unknown error occurred.", comment: "unknown")
        }
    }
}
