import Foundation
import Contacts

enum EmailAddressType: String {
    case custom = "CUSTOM"
    case home = "HOME"
    case iCloud = "ICLOUD"
    case other = "OTHER"
    case school = "SCHOOL"
    case work = "WORK"

    static func fromLabel(_ label: String?) -> EmailAddressType? {
        guard let label = label else {
            return nil
        }
        switch label {
        case CNLabelHome:
            return .home
        case CNLabelEmailiCloud:
            return .iCloud
        case CNLabelOther:
            return .other
        case CNLabelSchool:
            return .school
        case CNLabelWork:
            return .work
        default:
            return .custom
        }
    }

    func toLabel() -> String? {
        switch self {
        case .home:
            return CNLabelHome
        case .iCloud:
            return CNLabelEmailiCloud
        case .other:
            return CNLabelOther
        case .school:
            return CNLabelSchool
        case .work:
            return CNLabelWork
        default:
            return nil
        }
    }
}
