import Foundation
import Contacts

enum PostalAddressType: String {
    case custom = "CUSTOM"
    case home = "HOME"
    case other = "OTHER"
    case work = "WORK"

    static func fromLabel(_ label: String?) -> PostalAddressType? {
        guard let label = label else {
            return nil
        }
        switch label {
        case CNLabelHome:
            return .home
        case CNLabelOther:
            return .other
        case CNLabelWork:
            return .work
        default:
            return .custom
        }
    }

    func toLabel() -> String? {
        switch self {
        case .home:
            return CNLabelHome
        case .other:
            return CNLabelOther
        case .work:
            return CNLabelWork
        default:
            return nil
        }
    }
}
